from django.db import models


class Lien(models.Model):
    """Modèle pour les liens Linktree"""

    ICON_CHOICES = [
        # Réseaux sociaux
        ('facebook', 'Facebook'),
        ('instagram', 'Instagram'),
        ('linkedin', 'LinkedIn'),
        ('youtube', 'YouTube'),
        ('twitter', 'Twitter/X'),
        ('tiktok', 'TikTok'),
        ('snapchat', 'Snapchat'),
        ('pinterest', 'Pinterest'),
        ('whatsapp', 'WhatsApp'),
        ('telegram', 'Telegram'),
        ('line', 'Line'),
        ('discord', 'Discord'),
        ('twitch', 'Twitch'),

        # Icônes générales
        ('site-web', 'Site web'),
        ('email', 'Email'),
        ('telephone', 'Téléphone'),
        ('dossier', 'Dossier/Documents'),
        ('fichier', 'Fichier/PDF'),
        ('calendrier', 'Calendrier/Événement'),
        ('carte', 'Carte/Localisation'),
        ('contact', 'Contact'),
        ('info', 'Information'),
        ('question', 'FAQ/Aide'),
        ('lien', 'Lien générique'),
        ('etoile', 'Favoris/Important'),
        ('cœur', 'Cœur/Santé'),
        ('chat', 'Chat/Message'),
        ('video', 'Vidéo'),
        ('musique', 'Musique/Audio'),
        ('photo', 'Photo/Galerie'),
        ('news', 'Actualités'),
        ('alerte', 'Alerte/Notification'),
        ('check', 'Validation/Check'),
        ('telecharger', 'Téléchargement'),
        ('externe', 'Lien externe'),
    ]

    titre = models.CharField(
        max_length=100,
        verbose_name="Titre du lien",
        help_text="Texte affiché sur le bouton"
    )
    url = models.URLField(
        verbose_name="URL",
        help_text="Adresse complète du lien (https://...)"
    )
    icone = models.CharField(
        max_length=20,
        choices=ICON_CHOICES,
        default='lien',
        verbose_name="Icône",
        help_text="Icône affichée à côté du titre"
    )
    ordre = models.PositiveIntegerField(
        default=0,
        verbose_name="Ordre d'affichage",
        help_text="Les liens sont affichés par ordre croissant"
    )
    actif = models.BooleanField(
        default=True,
        verbose_name="Actif",
        help_text="Décocher pour masquer le lien"
    )
    date_creation = models.DateTimeField(auto_now_add=True)
    date_modification = models.DateTimeField(auto_now=True)

    class Meta:
        verbose_name = "Lien"
        verbose_name_plural = "Liens"
        ordering = ['ordre', 'titre']

    def __str__(self):
        return f"{self.titre} ({self.get_icone_display()})"

